//go:build ignore

package main

import (
	"fmt"
	"github.com/startfellows/tongo/tlb/parser"
	"os"
)

var bitsSizes = []int{96, 264, 320, 352}

func main() {

	s1 := parser.GenerateVarUintTypes(32)
	s2 := parser.GenerateConstantInts(64)
	s3 := parser.GenerateBitsTypes(bitsSizes)
	f, err := os.Create("integers.go")
	if err != nil {
		panic(err)
	}
	_, err = fmt.Fprint(f, `package tlb
// Code autogenerated. DO NOT EDIT. 

import (
	"github.com/startfellows/tongo/boc"
	"math/big"
)
`)
	if err != nil {
		panic(err)
	}
	_, err = f.WriteString(s1)
	if err != nil {
		panic(err)
	}
	_, err = f.WriteString(s2)
	if err != nil {
		panic(err)
	}
	_, err = f.WriteString(s3)
	if err != nil {
		panic(err)
	}
}
